// Type Declarations for Image File Imports

// Declare module for importing PNG image files
declare module "*.png" {
  const value: string;
  export default value;
}

// Declare module for importing GIF image files
declare module "*.gif" {
  const value: string;
  export default value;
}

// Declare module for importing SVG image files
declare module "*.svg" {
  const value: string;
  export default value;
}
